import React, { useState } from "react";


function App() {
  setInterval({updateTime},1000);
  const curDate=new Date();
  const now=curDate.toLocaleTimeString();
  const [time,setTime]=useState(now);

  function updateTime(){
    const newTime =newDate().toLocaleTimeString();
    setTime(newTime);
  }

  return(
    <h1>{time}</h1>
  )
}

export default App;
