import React from "react";


function App() {
  const curDate=new Date();
  function currentTime(){
    const time=curDate.toLocaleTimeString();
    return <h1>{time}</h1>;
  }
  setInterval(currentTime,1000);
  return <div>
    <h1>{currentTime}</h1>
  </div>
}

export default App;
